mapper.update:
update sys_dept_job sdj
set sdj.dept_id = #{deptId}
where sdj.job_Id = #{jobId};

mapper.delete:
delete
from sys_dept_job sdj
where sdj.job_id = #{jobId};

mapper.select:
SELECT j.job_id, j.job_name, j.begin_time, j.deadline, c.name FROM sys_job j LEFT JOIN sys_course c ON j.course_id=c.id LEFT JOIN sys_dept_job dj ON dj.job_id=j.job_id LEFT JOIN sys_dept_user du ON du.dept_id=dj.dept_id WHERE du.user_id=#{userId}
AND (FROM_UNIXTIME(j.deadline / 1000, '%m') = #{month} AND FROM_UNIXTIME(j.deadline / 1000, '%Y') = #{year})
AND j.job_name LIKE concat('%',#{keyword},'%')
AND (SELECT count(*) FROM job_logs jl WHERE jl.job_id = j.job_id) !=0
AND (SELECT count(*) FROM job_logs jl WHERE jl.job_id = j.job_id) =0
AND c.name=#{courseName}
